vim.filetype.add({  filename = {    ['env'] = 'sh',    ['gitconfig'] = 'gitconfig',    ['profile'] = 'sh',    ['vimrc'] = 'vim',    ['zaliases'] = 'zsh',    ['.*/zfunctions/.*'] = 'zsh',    ['.*/.zaliases/.*'] = 'zsh',    ['.*/.zscripts/.*'] = 'zsh',    ['.*/.profile/.*'] = 'zsh',    ['.*/.zshrc/.*'] = 'zsh',  },  pattern = {    ['.*%.env%..*'] = function(path, bufnr)      local contents = vim.api.nvim_buf_get_lines(bufnr, 0, -1, false)      return require('vim.filetype.detect').sh(path, contents)    end,  },})