return {  {    'junegunn/fzf',    build = './install --bin',    enabled = false,    lazy = false,  },  {    'ibhagwan/fzf-lua',    -- optional for icon support    dependencies = { 'nvim-tree/nvim-web-devicons' },    enabled = false,    lazy = false,    cmd = 'FzfLua',    keys = {      { '<leader>;', ':FzfLua live_grep<CR>', noremap = true, silent = true, desc = 'Live grep' },      { '<leader>gs', ':FzfLua grep_cword<CR>', noremap = true, silent = true, desc = 'Grep current word' },      { '<leader>b', ':FzfLua buffers<CR>', noremap = true, silent = true, desc = 'Buffers' },      { '<leader>l', ':FzfLua blines<CR>', noremap = true, silent = true, desc = 'Buffer lines' },      { 'ff', ':FzfLua files<CR>', noremap = true, silent = true, desc = 'Files' },      { '<leader>ts', ':FzfLua lsp_document_symbols<CR>', noremap = true, silent = true, desc = 'Files' },      { '<leader>fp', ':FzfLua git_files<CR>', noremap = true, silent = true, desc = 'Git files' },      { '<c-p>', ':GitProjects<CR>', noremap = true, silent = true, desc = 'Git files' },      { '<leader>g', ':FzfLua git_status<CR>', noremap = true, silent = true, desc = 'Git status' },      {        '<leader>ll',        ":FzfLua grep { search = vim.fn.input('GREP -> ') }<CR>",        noremap = true,        silent = true,        desc = 'Grep a word',      },    },    config = function()      require('fzf-lua').setup({        -- "telescope",        fzf_opts = { ['--preview-window'] = 'nohidden,down,50%' },        files = { cmd = 'rg --files' },        -- fzf_opts = { ['--layout'] = 'reverse-list' },        defaults = {          -- formatter = 'path.filename_first',        },        winopts = {          preview = {            default = 'bat',          },        },      })    end,  },}